import {RECEIVE_USERS, SET_CURRENT_USER, ADD_USER, LOGIN} from '../actions';

const isEmpty = require('is-empty');

const initState = {
    isLoggedIn: false,
    currentUser: 'admin?',
    user: {},
    units: [''],
    access: 0,
    loading: false,
};

export default (state = initState, {type, data = "default"}) => {
    switch (type) {
        case RECEIVE_USERS:
            return {
                ...state,
                // isLoggedIn: !isEmpty(action.payload),
                units: data
            };

        case SET_CURRENT_USER:
            console.log('>> Data SCU', data);
            return {
                ...state,
                // isLoggedIn: !isEmpty(action.payload),
                user: data,
                isLoggedIn: true,
                userID: data.id
            };

        case LOGIN:
            console.log('>> Data LOGIN', data);
            return {
                ...state,
                // isLoggedIn: !isEmpty(action.payload),
                isLoggedIn: true,
                userID: data.id
            };

        case ADD_USER:
            return {
                ...state,
                loading: false,
            };
        //
        // case USER_LOADING:
        //     return {
        //         ...state,
        //         loading: true
        //     };

        default:
            return state;
    }
}
