import { takeLatest, all, put, call } from 'redux-saga/effects';
import {REQUEST_USERS, receiveUsers, ADD_USER, addUser, SET_CURRENT_USER, LOGIN, login, loginSuccess} from '../actions';
import axios from 'axios'
import setAuthToken from "../../utils/setAuthToken";
import jwt from "jsonwebtoken";



// LOGIN + AUTH TOKEN
function callLogin(data) {
    return axios.post('/api/users/login', {email: data.email, password: data.password})
        .then(res => {
            localStorage.setItem('jwtToken', res.data.token);
            setAuthToken(res.data.token);
            console.log('>> Login ID', res);
        })
        .catch(err => {
            console.log('>>',err)
        });

    // return axios.post({
    //     method: 'post',
    //     url: '/api/users/login'
    // });
}

function* userLogin(action) {
    try {
        const responce = yield call(callLogin, action.data);
        console.log('>> responce', responce);
        if(responce) {
            console.log('have resp', responce);
            yield put(loginSuccess(responce.data))
        } else {
            console.log('nhr', responce);
        }
    } catch(e) {
        console.log('>> Login Error', e);
    }
}

function fetchUnits(data) {
    let result = [];

    axios.get('/api/users/units', data).then(data => {
        console.log('>> Success', data);
        result = data;
    }).catch(e => {
       console.log('>> Error', e);
    });

    return result;
}

function* getUsers(action) {
    try {
        const response = yield call(fetchUnits, action.data);
        const units = response.data;
        yield put(receiveUsers(units));
    } catch(e) {
        yield put(receiveUsers('Test was success! Error!'));
    }
}

function* addNewUser(action) {
    console.log('>> ANU');
    try {
        // const response = yield call(fetchUnits, action.data);
        // const units = response.data;
        // console.log('>> unitsGet', action, data);
        // yield put(receiveUsers(units));

        axios.post('/api/users/register', action.data)
            .then(res => {
                console.log('>> addUser', res);
            })
            .catch(errors => {
                if (errors.response) {
                   console.log('>> E2');
                } else if (errors.request) {
                    console.log('E', errors.request);
                } else {
                    console.log('Error', errors.message);
                }
            })
    } catch(e) {
        yield put(receiveUsers('Test was success! Error!'));
    }
}


function* watcher() {
    yield takeLatest(REQUEST_USERS, getUsers);
    yield takeLatest(ADD_USER, addNewUser);
    yield takeLatest(LOGIN, userLogin);
}

export default function* saga() {
    yield all([watcher()]);
}
